<?php/** * This is the model class for table "{{feedback}}". * * The followings are the available columns in table '{{feedback}}': * @property integer $id * @property string $name * @property string $tel * @property string $email * @property string $content * @property string $reply * @property integer $create_time * @property integer $update_time */class Feedback extends CActiveRecord{	/**	 * Returns the static model of the specified AR class.	 * @return Feedback the static model class	 */	public static function model($className=__CLASS__)	{		return parent::model($className);	}	/**	 * @return string the associated database table name	 */	public function tableName()	{		return '{{feedback}}';	}	/**	 * @return array validation rules for model attributes.	 */	public function rules()	{		// NOTE: you should only define rules for those attributes that		// will receive user inputs.		return array(			array('name, tel, email, content', 'required'),			array('create_time, update_time', 'numerical', 'integerOnly'=>true),			array('name', 'length', 'max'=>50),			array('tel, email', 'length', 'max'=>100),                        array('reply', 'length', 'max'=>1000),			// The following rule is used by search().			// Please remove those attributes that should not be searched.			array('id, name, tel, email, content, reply, create_time, update_time', 'safe', 'on'=>'search'),		);	}	/**	 * @return array relational rules.	 */	public function relations()	{		// NOTE: you may need to adjust the relation name and the related		// class name for the relations automatically generated below.		return array(		);	}	/**	 * @return array customized attribute labels (name=>label)	 */	public function attributeLabels()	{		return array(			'id' => 'ID',			'name' => '名字',			'tel' => '电话',			'email' => 'Email',			'content' => '内容',			'reply' => '回复',			'create_time' => '发布时间',			'update_time' => '更新时间',		);	}	/**	 * Retrieves a list of models based on the current search/filter conditions.	 * @return CActiveDataProvider the data provider that can return the models based on the search/filter conditions.	 */	public function search()	{		// Warning: Please modify the following code to remove attributes that		// should not be searched.		$criteria=new CDbCriteria;		$criteria->compare('id',$this->id);		$criteria->compare('name',$this->name,true);		$criteria->compare('tel',$this->tel,true);		$criteria->compare('email',$this->email,true);		$criteria->compare('content',$this->content,true);		$criteria->compare('reply',$this->reply,true);		$criteria->compare('create_time',$this->create_time);		$criteria->compare('update_time',$this->update_time);		return new CActiveDataProvider(get_class($this), array(			'criteria'=>$criteria,		));	}       public function beforeSave() {        if (parent::beforeSave()) {            if ($this->isNewRecord) {                $this->create_time = $this->update_time = time();            }            else                $this->update_time = time();            return true;        }        else            return false;    }}