<?php$this->pageTitle = Yii::app()->name . ' - ' . F::t('Feedback');$this->breadcrumbs = array(    F::t('Feedback'),);?><div class="page-title"><h1><?php echo F::t('Feedback') ?></h1></div><div class="std"><?php foreach ($feedback as $k => $f) { ?>    <ul>        <li style="margin-top:10px"><b>#<?php echo $k + 1 ?>楼</b>&nbsp;&nbsp;<?php echo $f->name ?>（<?php echo F::date($f->create_time) ?>）：            <?php echo CHtml::link($f->content, array('/feedback/view', 'id' => $f->id)) ?><p><?php if ($f->reply) { ?>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;管理员回复：                    <span style="color:#cd0a0a"><?php echo $f->reply ?></span><?php } ?></p></li>    </ul><?php } ?><h4><?php echo F::t('Create Feedback') ?></h4><ul>    <?php echo $this->renderPartial('_form', array('model' => $model)); ?></ul> </div>